import fromEntries from "lodash.frompairs";

export const important = target => {
    if (typeof target === "string") {
        return `${target} !important`;
    }
    return fromEntries(
        Object.entries(target).map(([key, value]) => [
            key,
            `${value} !important`,
        ]),
    );
};

